import rotation from "../App.js";
import offsets from "../App.js";
import points from "../App.js";
const fieldSizeInches = 144;
const fieldImagePixels = 600;
const scale = fieldSizeInches / fieldImagePixels;

const styles = {
  container: {
    position: "relative",
    display: "inline-block",
    width: 600,
    height: 600,
    userSelect: "none",
  },

  image: {
    width: 600,
    height: 600,
    transform: `rotate(${rotation}deg)`,
    transition: "transform 0.3s ease",
    pointerEvents: "none",
    userSelect: "none",
  },

  pathlines: {
    width: 600,
    height: 600,
    position: "absolute",
    inset: 0,
    pointerEvents: "none",
    zIndex: 0,
  },

  triangle: {
    position: "absolute",
    top: 2,
    left: "50%",
    transform: "translateX(-50%)",
    width: 0,
    height: 0,
    borderLeft: "6px solid transparent",
    borderRight: "6px solid transparent",
    borderBottom: "10px solid red",
    transformOrigin: "center",
  },
};

export default styles;
