// Field.js
import React, { useState, useRef, useEffect } from "react";
import image from "../assets/jerryfield.png"; // import the field image
import { getMirroredPoints } from "./src/components/inputs.js";

function Field({
  // props from App.js
  points,
  setPoints,
  offsets, // default robot size (for new points)
  rotation,
  fieldImagePixels,
  scale,
  handleClick,
  handleMouseDown,
  handleDoubleClick,
  handleMouseMove,
  handleMouseUp,
  // new props
  handleContextMenu, // right-click callback (e, index)
  selectedPointIndex, // index of point currently selected
}) {
  const [hoveredIndex, setHoveredIndex] = useState(null);
  const containerRef = useRef(null);
  const [menu, setMenu] = useState({
    visible: false,
    x: 0,
    y: 0,
    index: null,
  });
  const menuRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (
        menu.visible &&
        menuRef.current &&
        !menuRef.current.contains(e.target)
      ) {
        setMenu({ visible: false, x: 0, y: 0, index: null });
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [menu.visible]);

  /* reset hoveredIndex if its point disappears */
  useEffect(() => {
    if (hoveredIndex !== null && hoveredIndex >= points.length) {
      setHoveredIndex(null);
    }
  }, [points, hoveredIndex]);

  const visiblePoints = getMirroredPoints(points, mirrorOptions);

  return (
    <div
      ref={containerRef}
      style={{
        position: "relative",
        display: "inline-block",
        width: fieldImagePixels,
        height: fieldImagePixels,
        userSelect: "none",
      }}
      onClick={handleClick}
      onMouseMove={handleMouseMove}
      onMouseUp={handleMouseUp}
      className="image"
    >
      {/* field background */}
      <img
        src={image}
        alt="VEX Field"
        style={{
          width: fieldImagePixels,
          height: fieldImagePixels,
          transform: `rotate(${rotation}deg)`,
          transition: "transform 0.3s ease",
          pointerEvents: "none",
          //overflow: "visible",
          userSelect: "none",
        }}
        draggable={false}
      />

      {/* connecting lines */}
      <svg
        style={{
          width: fieldImagePixels,
          height: fieldImagePixels,
          position: "absolute",
          inset: 0,
          pointerEvents: "none",
          zIndex: 0,
        }}
      >
        {points.map((p, i) =>
          i === 0 ? null : (
            <line
              key={i}
              x1={points[i - 1].rawX}
              y1={points[i - 1].rawY}
              x2={p.rawX}
              y2={p.rawY}
              stroke="black"
              strokeWidth="2"
            />
          )
        )}

        {/* heading guide when hovering */}
        {hoveredIndex !== null &&
          points[hoveredIndex] &&
          (() => {
            const pt = points[hoveredIndex];
            const ang = (pt.heading ?? 0) + 90;
            const rad = (ang * Math.PI) / 180;
            const len = 300;
            const x2 = pt.rawX + len * Math.cos(rad);
            const y2 = pt.rawY - len * Math.sin(rad);
            return (
              <line
                key={`hover-heading`}
                x1={pt.rawX}
                y1={pt.rawY}
                x2={x2}
                y2={y2}
                stroke="rgba(255,0,0,0.6)"
                strokeWidth="2"
              />
            );
          })()}
      </svg>

      {/* every point */}
      {points.map((pt, i) => {
        /* robot placeholder rectangle dims */
        const boxW = (offsets.left + offsets.right) / scale;
        const boxH = (offsets.front + offsets.back) / scale;
        const top =
          pt.rawY - boxH / 2 + (offsets.front - offsets.back) / (2 * scale);
        const left =
          pt.rawX - boxW / 2 + (offsets.right - offsets.left) / (2 * scale);

        /* mouse-wheel rotates heading */
        const handleWheel = (e) => {
          e.preventDefault();
          e.stopPropagation();

          const delta = e.deltaY < 0 ? 1 : -1;
          const newHeading = ((pt.heading ?? 0) + delta + 360) % 360;

          const updatedPoints = [...points];
          updatedPoints[i] = {
            ...pt,
            heading: newHeading,
          };
          setPoints(updatedPoints);
        };

        const onCtx = (e) => {
          e.preventDefault();
          handleContextMenu(e, i); // tell App which point is selected
          setMenu({
            visible: true,
            x: e.clientX,
            y: e.clientY,
            index: i,
          });
        };

        return (
          <React.Fragment key={i}>
            {/* robot rectangle (rotated) */}
            <div
              style={{
                position: "absolute",
                top,
                left,
                width: boxW,
                height: boxH,
                pointerEvents: "none",
                transform: `rotate(${-(pt.heading ?? 0)}deg)`,
                transformOrigin: "center center",
              }}
            >
              <div
                style={{
                  width: "100%",
                  height: "100%",
                  border: "2px solid blue",
                  backgroundColor: "rgba(255,255,255,0.3)",
                }}
              />
              {/* points */}
              <div
                style={{
                  position: "absolute",
                  top: 2,
                  left: "50%",
                  transform: "translateX(-50%)",
                  width: 0,
                  height: 0,
                  borderLeft: "6px solid transparent",
                  borderRight: "6px solid transparent",
                  borderBottom: "10px solid red",
                }}
              />
            </div>

            {/* draggable dot */}
            <div
              onMouseDown={() => handleMouseDown(i)}
              onDoubleClick={(e) => {
                handleDoubleClick(i);
              }}
              onContextMenu={onCtx}
              onWheel={handleWheel}
              onMouseEnter={() => {
                setHoveredIndex(i);
                document.body.style.overflow = "hidden";
              }}
              onMouseLeave={() => {
                setHoveredIndex(null);
                //document.body.style.overflow = "";
              }}
              style={{
                position: "absolute",
                top: pt.rawY - 5,
                left: pt.rawX - 5,
                width: 10,
                height: 10,
                borderRadius: "50%",
                background:
                  selectedPointIndex === i && menu.visible ? "orange" : "red",
                cursor: "grab",
                zIndex: 2,
              }}
            />

            {/* coordinate label */}
            <div
              style={{
                position: "absolute",
                top: pt.rawY - 20,
                left: pt.rawX + 10,
                fontSize: 12,
                padding: "2px 4px",
                background: "black",
                borderRadius: 4,
                pointerEvents: "none",
                zIndex: 1,
                whiteSpace: "nowrap",
              }}
            >
              ({Math.round(pt.x)}, {Math.round(pt.y)}) @{" "}
              {Math.round(pt.heading ?? 0)}Â°
            </div>
          </React.Fragment>
        );
      })}

      {/* the context menu */}
      {menu.visible && points[menu.index] && (
        <div
          ref={menuRef}
          style={{
            position: "fixed",
            top: menu.y,
            left: menu.x,
            backgroundColor: "rgba(255, 255, 255, 0.4)",
            boxShadow: "3px 3px 3px 6px rgba(0,0,0,0.2)",
            zIndex: 100,
            padding: "4px",
            fontSize: "12px",
          }}
          onMouseLeave={() =>
            setMenu({ visible: false, x: 0, y: 0, index: null })
          }
          onClick={(e) => e.stopPropagation()}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              alignItems: "left",
              padding: "4px",
              color: "black",
            }}
          >
            {["intakeTime", "outtakeTime", "wait", "slew"].map((key) => (
              <div
                key={key}
                style={{ display: "flex", alignItems: "left", padding: "4px" }}
              >
                <label style={{ marginRight: "6px" }}>
                  {key.replace(/([A-Z])/g, " $1").toLowerCase()}:
                </label>
                <input
                  type="number"
                  value={points[menu.index]?.[key] ?? ""}
                  onChange={(e) => {
                    const val = e.target.value;
                    const newValue = val === "" ? "" : parseFloat(val);
                    const updated = [...points];
                    updated[menu.index] = {
                      ...updated[menu.index],
                      [key]: newValue,
                    };
                    setPoints(updated);
                  }}
                  style={{ width: "60px" }}
                />
              </div>
            ))}

            {["descore", "pneumatics", "hoardMotors", "ramDescore"].map(
              (key) => (
                <div
                  key={key}
                  style={{
                    display: "flex",
                    alignItems: "left",
                    padding: "4px",
                  }}
                >
                  <label style={{ marginRight: "6px" }}>
                    {key.replace(/([A-Z])/g, " $1").toLowerCase()}:
                  </label>
                  {key === "descore" ? (
                    <input
                      type="checkbox"
                      checked={!!points[menu.index]?.descore}
                      onChange={(e) => {
                        const updated = [...points];
                        updated[menu.index] = {
                          ...updated[menu.index],
                          descore: e.target.checked,
                        };
                        setPoints(updated);
                      }}
                    />
                  ) : (
                    <select
                      value={points[menu.index]?.[key] ?? ""}
                      onChange={(e) => {
                        const updated = [...points];
                        updated[menu.index] = {
                          ...updated[menu.index],
                          [key]: e.target.value === "" ? null : e.target.value,
                        };
                        setPoints(updated);
                      }}
                    >
                      <option value="">--</option>
                      <option value="true">true</option>
                      <option value="false">false</option>
                      {key === "pneumatics" && (
                        <>
                          <option value="high">high</option>
                          <option value="low">low</option>
                        </>
                      )}
                    </select>
                  )}
                </div>
              )
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default Field;
