import rotation from "../App.js";
import offsets from "../App.js";

const styles = {
  container: {
    position: "relative",
    display: "inline-block",
    width: 600,
    height: 600,
    userSelect: "none",
  },

  image: {
    width: 600,
    height: 600,
    transform: `rotate(${rotation}deg)`,
    transition: "transform 0.3s ease",
    pointerEvents: "none",
    userSelect: "none",
  },

  pathlines: {
    width: 600,
    height: 600,
    position: "absolute",
    inset: 0,
    pointerEvents: "none",
    zIndex: 0,
  },

  robotSizing: {
    /* robot placeholder rectangle dims */
    boxW: (offsets.left + offsets.right) / scale,
    boxH: (offsets.front + offsets.back) / scale,
    top: pt.rawY - boxH / 2 + (offsets.front - offsets.back) / (2 * scale),
    left: pt.rawX - boxW / 2 + (offsets.right - offsets.left) / (2 * scale),
  },
};

export default styles;
