function generateEZTemplateAuton(points, mirrorOptions) {
  let codeLines = [];
  codeLines.push("// EZ-Template Auton Path");

  points.forEach((point, i) => {
    const x = point.x;
    const y = point.y;
    const heading = point.heading;
    const actions = point.actions;

    if (mirrorOptions.y) codeLines.push("odom_y_flip();");
    if (mirrorOptions.x) codeLines.push("odom_x_flip();");
    if (mirrorOptions.theta) codeLines.push("odom_theta_flip();");

    if (i === 0)
      codeLines.push(`chassis.odom_xyt_set(${x} , ${y}, ${heading});`);
    else if (point.direction === "fwd") {
      codeLines.push(`  // Move to point ${i + 1}`);
      codeLines.push(
        `chassis.pid_odom_set({{ ${x} , ${y} }, fwd, ${heading}});`
      );
    } else {
      codeLines.push(`  // Move to point ${i + 1}`);
      codeLines.push(
        `chassis.pid_odom_set({{ ${x} , ${y} }, rev, ${heading}});`
      );
    }

    if (actions.intakeTime !== undefined) {
      codeLines.push(
        `intakeMotor.moveFor( ${actions.intakeTime} seconds, dir: 1)})`
      );
    } else if (actions.outtakeTime > 0) {
      codeLines.push(
        `intakeMotor.moveFor( ${actions.outtakeTime} seconds, dir: -1)})`
      );
    }
    //if (defaultActions().)
  });

  codeLines.push("chassis.pid_wait(); }");

  return codeLines.join("\n");
}

export { generateEZTemplateAuton };
