export function odom_x_flip(points) {
  return points.map((pt) => ({
    ...pt,
    x: -pt.x,
    heading: (180 - pt.heading + 360) % 360,
  }));
}

export function odom_y_flip(points) {
  return points.map((pt) => ({
    ...pt,
    y: -pt.y,
    heading: (360 - pt.heading) % 360,
  }));
}

export function odom_theta_flip(points) {
  return points.map((pt) => ({
    ...pt,
    heading: (pt.heading + 180) % 360,
  }));
}
