import React, { useState, useRef, useEffect } from "react";
import "./App.css";
import { generateEZTemplateAuton } from "./utils/ezlogic.js";
import Field from "./components/field.js";
import Inputs from "./components/inputs.js";
import CodeOutput from "./components/outputs.js";
//import Subsystems from "./components/subsystems.js";

//helper for "actions" object
const defaultActions = () => ({
  "intake how many blocks?": 0, //number of blocks
  "outtake how many blocks?": 0, //number of blocks
  "stop here for (ms)": 0, //ms
  "toggle outtaking piston": null, // "high" | "low"
  slew: false, // boolean
  "dir get to this point?": "fwd", // "fwd" or "rev"
});

function App() {
  const [field, setField] = useState("auton");
  const [rotation, setRotation] = useState(0); // 0 for no rotation, 90 for blue alliance, 270 for red alliance
  const [points, setPoints] = useState([]); // array of points with {x, y, rawX, rawY, heading, direction}
  const [offsets, setOffsets] = useState({
    //distances from rotational center to robot edges, makes placeholder rectangle
    left: "",
    right: "",
    back: "",
    front: "",
  });
  const [currentHeading, setCurrentHeading] = useState(0); // array for point headings
  const [currentDirection, setCurrentDirection] = useState(1); // "fwd" or "rev" for forward or reverse direction
  const [selectedPointIndex, setSelectedPointIndex] = useState(null);
  //refs to track dragging state
  const draggingPointIndex = useRef(null);
  const isDragging = useRef(false); //currently dragging a point
  const wasDragging = useRef(false); //when wasDragging is true, it means a user was dragging a point, so the next click should not add a point

  //constants for field size and scale
  const fieldSizeInches = 144;
  const fieldImagePixels = 600;
  const scale = fieldSizeInches / fieldImagePixels;

  const [mirroredCorner, setMirroredCorner] = useState(false);
  const [editCode, setEditCode] = useState("paste in code to test path");

  const isRobotInBounds = (dx, dy) => {
    const robotLeft = dx - offsets.left;
    const robotRight = dx + offsets.right;
    const robotBack = -dy - offsets.back;
    const robotFront = -dy + offsets.front;

    return (
      robotLeft >= -72 &&
      robotRight <= 72 &&
      robotBack >= -72 &&
      robotFront <= 72
    );
  };

  //used to prevent double clicks from adding points instead of deleting
  const clickTimeout = useRef(null);
  useEffect(() => {
    const stored = localStorage.getItem("points");
    if (stored) {
      setPoints(JSON.parse(stored));
    }
  }, []);
  useEffect(() => {
    localStorage.setItem("points", JSON.stringify(points));
  }, [points]);

  // updates offsets on input change
  const handleOffsetChange = (e) => {
    const { name, value } = e.target; //gets label (left, right, back, front) and value from input
    setOffsets((prev) => ({
      ...prev, //copies previous offsets
      [name]: value === "" ? "" : parseFloat(value),
      //the name becomes the key
      //if the input is empty, set to empty string
      //otherwise parse the value to a float
    }));
  };

  //if the user clicks on a point, start dragging it
  //handleMouseDown = click on an already existing point
  const handleMouseDown = (index) => {
    draggingPointIndex.current = index; //index = index of point clicked from points array
    isDragging.current = true;
    wasDragging.current = false;
  };

  //when user moves a point
  const handleMouseMove = (e) => {
    if (!isDragging.current || draggingPointIndex.current === null) return;
    //if not currently dragging or no point has been dragged, ignore
    wasDragging.current = true;
    //else set wasDragging to true so next click does not add a point

    const container = e.currentTarget; //e.currentTarget is the point currently being dragged
    const rect = container.getBoundingClientRect(); //info about the container's pos and size

    let rawX = e.clientX - rect.left; //how far the mouse is from the left edge of the image
    let rawY = e.clientY - rect.top; //how far the mouse is from the top edge of the image

    //clamp rawX and rawY to be within the image bounds
    rawX = Math.max(0, Math.min(rawX, rect.width));
    rawY = Math.max(0, Math.min(rawY, rect.height));

    //sets center of the field as (0, 0), converts from pixels to inches
    const dx = (rawX - rect.width / 2) * scale;
    const dy = (rawY - rect.height / 2) * scale;

    //checks that robot placeholder is fully inside field
    if (!isRobotInBounds(dx, dy)) return;

    const updated = [...points]; //create a copy of points array to update
    updated[draggingPointIndex.current] = {
      //at the index of the point being dragged
      ...updated[draggingPointIndex.current], //store existing properties
      x: dx, //also stores coordinates in inches
      y: -dy, //negative y because dy is distance from top, so it becomes more positive as you go down
      //used for labeling on ui
      rawX, //raw coords for displaying on field
      rawY,
    };
    setPoints(updated);
  };

  // Mouse up - finish dragging
  const handleMouseUp = () => {
    draggingPointIndex.current = null;
    setTimeout(() => {
      isDragging.current = false;
      wasDragging.current = false;
    }, 10);
  };

  // Double click to delete point
  const handleDoubleClick = (index) => {
    if (clickTimeout.current) {
      clearTimeout(clickTimeout.current);
      clickTimeout.current = null;
    }
    setPoints(points.filter((_, i) => i !== index));
  };

  // Click on empty field to add a point (only if not dragging)
  const handleClick = (e) => {
    if (wasDragging.current) return;
    if (clickTimeout.current) return;

    const rect = e.currentTarget.getBoundingClientRect();
    const rawX = e.clientX - rect.left;
    const rawY = e.clientY - rect.top;

    clickTimeout.current = setTimeout(() => {
      clickTimeout.current = null;

      // Out of bounds, ignore
      if (rawX < 0 || rawY < 0 || rawX > rect.width || rawY > rect.height)
        return;

      const dx = (rawX - rect.width / 2) * scale;
      const dy = (rawY - rect.height / 2) * scale;

      if (!isRobotInBounds(dx, dy)) return;

      setPoints([
        ...points,
        {
          x: dx, // round to 3 decimal places
          y: -dy,
          rawX,
          rawY,
          heading: currentHeading,
          direction: currentDirection,
          offsets,
          actions: defaultActions(),
        },
      ]);
    }, 200);
  };

  // Heading input change
  const handleHeadingChange = (e) => {
    const newHeading = parseFloat(e.target.value) || 0;
    setCurrentHeading(newHeading);

    if (points.length > 0) {
      const updated = [...points];
      updated[points.length - 1] = {
        ...updated[points.length - 1],
        heading: newHeading,
      };
      setPoints(updated);
    }
  };

  // Direction input change
  const handleDirectionChange = (e) => {
    const newDirection = e.target.value; // string "fwd" or "rev"
    setCurrentDirection(newDirection);

    if (points.length > 0) {
      const updated = [...points];
      updated[points.length - 1] = {
        ...updated[points.length - 1],
        direction: newDirection,
      };
      setPoints(updated);
    }
  };

  const handleContextMenu = (e, index) => {
    e.preventDefault(); // stop the right-click menu
    setSelectedPointIndex(index);
  };

  return (
    <div className="App" style={{ userSelect: "none" }}>
      <h1>vex auton tool</h1>
      <p>rn the subsystems functions are pseudocoded in</p>
      <Inputs
        rotation={rotation}
        setRotation={setRotation}
        setPoints={setPoints}
        offsets={offsets}
        handleOffsetChange={handleOffsetChange}
        currentHeading={currentHeading}
        handleHeadingChange={handleHeadingChange}
        currentDirection={currentDirection}
        handleDirectionChange={handleDirectionChange}
        points={points}
        fieldImagePixels={fieldImagePixels}
        scale={scale}
        mirroredCorner={mirroredCorner}
        setMirroredCorner={setMirroredCorner}
        field={field}
        setField={setField}
      />

      <div
        style={{
          display: "flex",
          alignItems: "flex-start",
          gap: "20px",
          maxWidth: "1100px",
          margin: "20px",
        }}
      >
        <Field
          points={points}
          setPoints={setPoints}
          rotation={rotation}
          offsets={offsets}
          scale={scale}
          handleClick={handleClick}
          handleMouseDown={handleMouseDown}
          handleDoubleClick={handleDoubleClick}
          handleMouseMove={handleMouseMove}
          handleMouseUp={handleMouseUp}
          handleContextMenu={handleContextMenu}
          selectedPointIndex={selectedPointIndex}
          mirroredCorner={mirroredCorner}
          field={field}
        />
        <CodeOutput
          code={generateEZTemplateAuton({ points, mirroredCorner })}
          editCode={editCode}
          setEditCode={setEditCode}
        />
      </div>
    </div>
  );
}

export default App;
