export default function fieldToPixel(
  x,
  y,
  fieldSizePixels,
  rotationDegrees,
  scale
) {
  const center = fieldSizePixels / 2;

  // Convert field inches to initial pixel position
  let px = center + x / scale;
  let py = center - y / scale;

  // Apply field rotation
  const angleRad = (rotationDegrees * Math.PI) / 180;
  const dx = px - center;
  const dy = py - center;

  const rotatedX = center + dx * Math.cos(angleRad) - dy * Math.sin(angleRad);
  const rotatedY = center + dx * Math.sin(angleRad) + dy * Math.cos(angleRad);

  return { rawX: rotatedX, rawY: rotatedY };
}
