import React, { useState } from "react";
import SyntaxHighlighter from "react-syntax-highlighter";
import { darcula } from "react-syntax-highlighter/dist/esm/styles/hljs";

function CodeOutput({ code }) {
  const [copied, setCopied] = useState(false);

  const handleCopy = () => {
    if (!navigator.clipboard) {
      alert("Clipboard API not supported");
      return;
    }
    navigator.clipboard
      .writeText(code)
      .then(() => {
        setCopied(true);
        setTimeout(() => setCopied(false), 500);
      })
      .catch(() => {
        alert("uh oh!");
      });
  };

  return (
    <div className="code">
      <button
        onClick={handleCopy}
        style={{
          position: "absolute",
          top: 200,
          right: 80,
          padding: "4px 8px",
          fontSize: "0.8rem",
          cursor: "pointer",
          borderRadius: "4px",
          border: "none",
          backgroundColor: copied ? "green" : "#007bff",
          color: "white",
          userSelect: "none",
          zIndex: 10,
        }}
      >
        {copied ? "done" : "copy code to clipboard"}
      </button>

      <pre
        style={{
          whiteSpace: "pre-wrap",
          userSelect: "text",
        }}
      >
        <SyntaxHighlighter
          language="c"
          style={darcula}
          wrapLines={true}
          showLineNumbers={true}
        >
          {code}
        </SyntaxHighlighter>
      </pre>
    </div>
  );
}

export default CodeOutput;
