import React, { useState } from "react";
import SyntaxHighlighter from "react-syntax-highlighter";
import { obsidian } from "react-syntax-highlighter/dist/esm/styles/hljs";

function CodeOutput({ code, editCode, setEditCode }) {
  const [copied, setCopied] = useState(false);

  const handleCopy = () => {
    if (!navigator.clipboard) {
      alert("Clipboard API not supported");
      return;
    }
    navigator.clipboard
      .writeText(code)
      .then(() => {
        setCopied(true);
        setTimeout(() => setCopied(false), 500);
      })
      .catch(() => {
        alert("uh oh!");
      });
  };

  return (
    <div className="code">
      <button
        onClick={handleCopy}
        style={{
          position: "absolute",
          top: 200,
          right: 80,
          padding: "4px 8px",
          fontSize: "0.8rem",
          cursor: "pointer",
          borderRadius: "4px",
          border: "none",
          backgroundColor: copied ? "green" : "#007bff",
          color: "white",
          userSelect: "none",
          zIndex: 10,
        }}
      >
        {copied ? "done" : "copy code to clipboard"}
      </button>

      <pre
        style={{
          whiteSpace: "pre-wrap",
          userSelect: "text",
          fontSize: "0.8rem",
          backgroundColor: "rgba(0, 0, 0, 0.0)",
        }}
      >
        <div className="code-block">
          <SyntaxHighlighter
            language="c"
            style={obsidian}
            wrapLines={true}
            showLineNumbers={true}
          >
            {code}
          </SyntaxHighlighter>
        </div>

        <div
          style={{
            fontFamily: "Arial, sans-serif",
            marginTop: "1em",
            fontSize: "14px",
          }}
        >
          paste written code below to test premade path. clear the textarea to
          keep creating path on field.
          <textarea
            value={editCode}
            onChange={(e) => setEditCode(e.target.value)}
            style={{
              width: "100%",
              height: "300px",
              fontFamily: "monospace",
              fontSize: "14px",
              marginTop: "20px",
              whiteSpace: "pre",
              backgroundColor: "rgba(0, 0, 0, 0.1)",
              color: "white",
            }}
          />
        </div>
      </pre>
    </div>
  );
}

export default CodeOutput;
