import fieldToPixel from "./coordUtils.js";
import * as styles from "./styling.js";

export function odom_x_flip(points, fieldImagePixels, rotation, scale) {
  return points.map((pt) => {
    const newX = -pt.x;
    const { rawX, rawY } = fieldToPixel(
      newX,
      pt.y,
      fieldImagePixels,
      rotation,
      scale
    );
    return { ...pt, x: newX, rawX, rawY };
  });
}

export function odom_y_flip(points, fieldImagePixels, rotation, scale) {
  return points.map((pt) => {
    const newY = -pt.y;
    const { rawX, rawY } = fieldToPixel(
      pt.x,
      newY,
      fieldImagePixels,
      rotation,
      scale
    );
    return { ...pt, y: newY, rawX, rawY };
  });
}

export function odom_theta_flip(points) {
  return points.map((pt) => ({
    ...pt,
    heading: (pt.heading + 180) % 360,
  }));
}
