import { mirroredCorner } from "../App.js";

function Inputs({
  rotation,
  setRotation,
  setPoints,
  offsets,
  handleOffsetChange,
  currentHeading,
  handleHeadingChange,
  currentDirection,
  handleDirectionChange,
}) {
  return (
    <>
      {/* first line of buttons */}
      <div style={{ marginBottom: "20px" }}>
        {/* rotate for red alliance */}
        <button
          onClick={() => setRotation(270)}
          style={{ backgroundColor: "rgb(247, 82, 70)", color: "white" }}
        >
          red alliance
        </button>

        {/* rotate for blue alliance */}
        <button
          onClick={() => setRotation(90)}
          style={{
            marginLeft: "10px",
            backgroundColor: "rgb(70, 123, 247)",
            color: "white",
          }}
        >
          blue alliance
        </button>

        {/* clear points */}
        <button onClick={() => setPoints([])} style={{ marginLeft: "10px" }}>
          clear points
        </button>

        {/* mirror points */}
        <button
          onClick={() => (mirroredCorner = !mirroredCorner)}
          style={{ marginLeft: "10px" }}
        >
          flip auton for opposite corner
        </button>
      </div>
      distance from edge to odom center (inches):
      <div style={{ marginBottom: "20px" }}>
        {["left", "right", "back", "front"].map((side) => (
          <label key={side} style={{ marginRight: "10px" }}>
            {side}:
            <input
              type="number"
              step="0.001"
              name={side}
              value={offsets[side]}
              onChange={handleOffsetChange}
              style={{ marginLeft: "4px" }}
            />
          </label>
        ))}
      </div>
    </>
  );
}

export default Inputs;
