function generateEZTemplateAuton(points) {
  let codeLines = [];
  codeLines.push("// EZ-Template Auton Path");
  codeLines.push("chassis.pid_odom_behavior_set(ez::shortest);");
  codeLines.push("");

  points.forEach((point, i) => {
    const x = point.x;
    const y = point.y;
    const heading = point.heading;
    const actions = point.actions;
    const intake = point["intake how many blocks?"];
    const outtake = point["outtake how many blocks?"];
    const wait = point.actions["stop here for (ms)"];
    const pneumatics = point.actions["toggle outtaking piston"];
    const slew = point.slew;
    const direction = point.actions["dir to next point?"];

    if (i === 0) {
      codeLines.push(`//starting pos:`);
      codeLines.push(`chassis.odom_xyt_set(${x} , ${y}, ${heading});`);
      codeLines.push(``);
    } else if (point.direction === "fwd") {
      codeLines.push(`  // Move to point ${i + 1}`);
      codeLines.push(
        `chassis.pid_odom_set({{ ${x} , ${y} }, ${direction}, ${heading}}, ${slew});`
      );
    } else {
      codeLines.push(`  // Move to point ${i + 1}`);
      codeLines.push(
        `chassis.pid_odom_set({{ ${x} , ${y} }, ${direction}, ${heading}});`
      );
    }

    if (actions.intakeTime !== undefined) {
      codeLines.push(
        `intakeMotor.moveFor( ${actions.intakeTime} seconds, dir: 1)})`
      );
    } else if (actions.outtakeTime > 0) {
      codeLines.push(
        `intakeMotor.moveFor( ${actions.outtakeTime} seconds, dir: -1)})`
      );
    }
    //if (defaultActions().)
  });

  codeLines.push("chassis.pid_wait(); }");

  return codeLines.join("\n");
}

export { generateEZTemplateAuton };
