const [flipMode, setFlipMode] = useState("none");
// values: "none", "corner", "alliance", "both"

function odom_flip_x(x) {
  if (flipMode === "alliance" || flipMode === "both") return -x;
  return x;
}

function odom_flip_y(y) {
  if (flipMode === "corner" || flipMode === "both") return -y;
  return y;
}

function odom_flip_theta(theta) {
  if (flipMode === "both") return normalizeHeading(theta + 180);
  if (flipMode === "corner" || flipMode === "alliance")
    return normalizeHeading(-theta);
  return normalizeHeading(theta);
}

function normalizeHeading(angle) {
  return ((angle % 360) + 360) % 360;
}
