import rotation from "../App.js";

const styles = {
  container: {
    position: "relative",
    display: "inline-block",
    width: fieldImagePixels,
    height: fieldImagePixels,
    userSelect: "none",
  },

  image: {
    width: 600,
    height: 600,
    transform: `rotate(${rotation}deg)`,
    transition: "transform 0.3s ease",
    pointerEvents: "none",
    userSelect: "none",
  },

  pathlines: {
    width: 600,
    height: 600,
    position: "absolute",
    inset: 0,
    pointerEvents: "none",
    zIndex: 0,
  },
};

export default styles;
