import React, { useState } from "react";

function CodeOutput({ code }) {
  const [copied, setCopied] = useState(false);

  const handleCopy = () => {
    if (!navigator.clipboard) {
      alert("Clipboard API not supported");
      return;
    }
    navigator.clipboard
      .writeText(code)
      .then(() => {
        setCopied(true);
        setTimeout(() => setCopied(false), 500);
      })
      .catch(() => {
        alert("uh oh!");
      });
  };

  return (
    <div className="code" style={{ position: "relative" }}>
      <button
        onClick={handleCopy}
        style={{
          position: "fixed",
          top: 230,
          right: 230,
          padding: "4px 8px",
          fontSize: "0.8rem",
          cursor: "pointer",
          borderRadius: "4px",
          border: "none",
          backgroundColor: copied ? "green" : "#007bff",
          color: "white",
          userSelect: "none",
          zIndex: 10,
        }}
      >
        {copied ? "oki done :3" : "copy code to clipboard"}
      </button>

      <pre style={{ whiteSpace: "pre-wrap", paddingTop: "30px" }}>{code}</pre>
    </div>
  );
}

export default CodeOutput;
