import { odom_x_flip, odom_y_flip, odom_theta_flip } from "../utils/mirroring";

function Inputs({
  rotation,
  setRotation,
  setPoints,
  offsets,
  handleOffsetChange,
  currentHeading,
  handleHeadingChange,
  currentDirection,
  handleDirectionChange,
}) {
  return (
    <>
      {/* first line of buttons */}
      <div style={{ marginBottom: "20px" }}>
        {/* rotate for red alliance */}
        <button
          onClick={() => setRotation(270)}
          style={{ backgroundColor: "rgb(247, 82, 70)", color: "white" }}
        >
          red alliance
        </button>

        {/* rotate for blue alliance */}
        <button
          onClick={() => setRotation(90)}
          style={{
            marginLeft: "10px",
            backgroundColor: "rgb(70, 123, 247)",
            color: "white",
          }}
        >
          blue alliance
        </button>

        {/* clear points */}
        <button onClick={() => setPoints([])} style={{ marginLeft: "10px" }}>
          clear points
        </button>

        {/* mirror points */}
        <button
          onClick={() => setPoints((points) => odom_y_flip(points))}
          style={{ marginLeft: "10px" }}
        >
          flip corner (on same alliance)
        </button>

        <button
          onClick={() =>
            setPoints((points) => odom_theta_flip(odom_x_flip(points)))
          }
          style={{ marginLeft: "10px" }}
        >
          flip alliance (on same corner)
        </button>

        <button
          onClick={() =>
            setPoints((points) =>
              odom_theta_flip(odom_y_flip(odom_x_flip(points)))
            )
          }
          style={{ marginLeft: "10px" }}
        >
          flip both (on opposite corner and alliance)
        </button>
      </div>

      {/* second line of inputs for heading and direction */}
      <div style={{ marginBottom: "20px" }}>
        {["left", "right", "back", "front"].map((side) => (
          <label key={side} style={{ marginRight: "10px" }}>
            {side}:
            <input
              type="number"
              step="0.001"
              name={side}
              value={offsets[side]}
              onChange={handleOffsetChange}
              style={{ marginLeft: "4px" }}
            />
          </label>
        ))}
        <br />
        <label style={{ marginLeft: "0px", marginTop: "50px" }}>
          heading of last added point:
          <input
            type="number"
            name="heading"
            value={currentHeading}
            onChange={handleHeadingChange}
            style={{ marginLeft: "4px", width: "60px" }}
          />
        </label>
        <label style={{ marginLeft: "20px", marginTop: "50px" }}>
          direction robot should travel to get to the most recently added point:
          <select
            name="direction"
            value={currentDirection}
            onChange={handleDirectionChange}
            style={{ marginLeft: "4px", width: "100px" }}
          >
            <option value="fwd">Forward</option>
            <option value="rev">Reverse</option>
          </select>
        </label>
      </div>
    </>
  );
}

export default Inputs;
